import React, { useState } from "react";
const Card = ({ titulo, descripcion, asignado, fecha, status }) => {
  const getCardColor = () => {
    switch (status) {
      case "pending":
        return "bg-warning";
      case "canceled":
        return "bg-danger";
      case "success":
        return "bg-success";
      default:
        return "bg-light";
    }
  };
  const [warning, setWarning] = useState();
  const handleOnClick = (e) => {
    e.preventDefault();
  };
  return (
    <div className="col">
      <div className={`card text-dark ${getCardColor()} shadow-sm`}>
        <div className="row">
          <div className="col">
            <div className="card-header d-flex align-items-center">
              {titulo}
              <div className="d-flex ms-auto custom-botton ">
                <button
                  type="button"
                  className="btn btn-warning shadow-sm"
                  title="Pendiente"
                  id="pendiente"
                  onClick={(e) => setWarning(e.target.value)}
                />
                <button
                  type="button"
                  className="btn btn-success shadow-sm"
                  title="Realizada"
                  id="realizada"
                />
                <button
                  type="button"
                  className="btn btn-danger shadow-sm"
                  title="Cancelar"
                  id="cancelar"
                />
              </div>
            </div>
          </div>
          <div className="card-body">
            <h5 className="card-title">{descripcion}</h5>
            <p className="card-text">{asignado}</p>
            <h6>{fecha}</h6>
          </div>
        </div>
      </div>
    </div>
  );
};

export { Card };
