import React, { useState, useEffect } from "react";
import { Layout, Main } from "../../components";
import { api } from "../../utilities";
import { objectToArray } from "../../helpers";
import "./tareas.css";
const Tareas = ({ titulo }) => {
  const [tareas, setTareas] = useState([]);

  const traer = () => {
    api
      .get("/tareas.json")
      .then((response) => {
        console.log(response);
        // setTitulo({ titulo: data });
        const datos = objectToArray(response.data);
        setTareas(datos);
        // a tareas le asignamos el valor datos
      })
      .catch((error) => console.log(error));
  };

  useEffect(() => {
    traer();
  }, []);
  return (
    <Layout>
      <Main showButtom={true} titulo="Lista de Tareas">
        <div className="row row-cols-1 row-cols-md-3 g-4">
          {tareas.map(({ titulo, descripcion, asignado, fecha }) => {
            return (
              <div className="col">
                <div className="card text-dark bg-light">
                  {/* aqui queremos poner los switch con los estados de las tareas */}
                  <div className="row">
                    <div className="col">
                      <div className="card-header d-flex align-items-center">
                        {titulo}
                        <div className="d-flex ms-auto custom-botton">
                          <button
                            type="button"
                            className="btn btn-warning"
                            title="Pendiente"
                            id="pendiente"
                          />
                          <button
                            type="button"
                            className="btn btn-success"
                            title="Realizada"
                            id="realizada"
                          />
                          <button
                            type="button"
                            className="btn btn-danger"
                            title="Cancelar"
                            id="cancelar"
                          />
                        </div>
                      </div>
                    </div>
                    <div className="card-body">
                      <h5 className="card-title">{descripcion}</h5>
                      <p className="card-text">{asignado}</p>
                      <h6>{fecha}</h6>
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </Main>
    </Layout>
  );
};

export { Tareas };
