import React from "react";
import "./header.css";
// esto es { showButtom, titulo, handleOnClick } = props
// podria destructurarlo en la linea siguiente asi: const {showButtom, titulo, handleOnClick} = props;
const Header = ({ showButtom, titulo, handleOnClick }) => {
  return (
    <div className="d-flex align-items-center justify-content-between mb-4 ms-4 me-4">
      <h1 className="h3 mb-0 text-grey-800">{titulo}</h1>
      {showButtom && (
        <button
          onClick={handleOnClick}
          className="d-none d-sm-inline-block  btn btn-danger btn-rounded"
        >
          Agregar
        </button>
      )}
    </div>
  );
};

Header.defaultProps = {
  showButtom: false,
};
export { Header };
